INSERT INTO `archives` VALUES ('596', '1', '1', '590', '零食工坊加盟_零食工坊加盟电话', '零食工坊', '零食工坊', '', '2017-03-17 09:34:06', '1', '零食工坊', '&lt;p&gt;　　零食工坊致力于打造中国最具价值的零食专卖连锁品牌，拥有精品零食开发、加工、销售三位一体的完整产业链，公司凭借世界先进的“连锁品牌专卖”经营模式，遵循“名、特、精、优”的经营原则，充分挖掘世界各地特色零食文化，整合优势资源，为消费者带来健康、天然、快乐、时尚的生活享受！零食工坊自2007年3', '蔡了', '/images/thread/2017/03/17/8d4fe0d8969dbf6669b571394d206efb.jpg', '1', '2017-03-17 09:34:06', '2017-03-17 09:34:06', 'p,p');
